}
.node-tree .node {
	padding: calc(var(--text) / 4) var(--zero) calc(var(--text) / 4) calc(var(--text) * 1.5);
}
.node-tree .node-tree {
	padding: var(--zero);
}
.node-tree .node .expand-collapse {
	height: var(--text);
	font-size: var(--text);
